def mergesort(list):
    last = len(list)
    mid = int(last/2)
    if last > 1:
        return(merge(mergesort(list[:mid]),mergesort(list[mid:])))
    else:
        return(merge(list[:mid], list[mid:]))

def merge(list1, list2):
    #print(list1)
    #print(list2)
    mergedlist = []
    while list1 != [] or list2 !=[]:
        if list1 == []:
            mergedlist.append(list2[0])
            list2.pop(0)
            print(mergedlist)
        elif list2 == []:
            mergedlist.append(list1[0])
            list1.pop(0)
            print(mergedlist)
        elif list1[0] <= list2[0]:
            mergedlist.append(list1[0])
            list1.pop(0)
            print(mergedlist)
        elif list2[0] < list1[0]:
            mergedlist.append(list2[0])
            list2.pop(0)
            print(mergedlist)
    return(mergedlist)
